import { Component } from '@angular/core';
import { TarefasServiceService } from './services/tarefas/tarefas-service.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Tarefas-front';
  tarefa: string = '';

  constructor(private TarefasServiceService: TarefasServiceService){}

  onSubmit() {
    const tarefaData = {
      tarefa: this.tarefa,
      status: false
    };

    this.TarefasServiceService.adicionarTarefa(tarefaData).subscribe(response => {
      console.log('Tarefa adicionada com sucesso', response);
    }, error => {
      console.log('Erro ao adicionar a tarefa', error);
    });

    this.tarefa = '';
  }

}
<header>
  <h1>Lista de Tarefas</h1>
</header>
<section>
  <h2 id="subtitle">ADICIONAR TAREFAS</h2>
  <form (ngSubmit)="onSubmit()">
    <input type="text" [(ngModel)]="tarefa" name="tarefa" required>
    <button type="submit">Adicionar tarefa</button>
  </form>
</section>
<section>
  <table>
    <thead>
      <tr>
        <th>Nº</th>
        <th>DESCRIÇÃO DA TAREFA</th>
        <th>STATUS</th>
      </tr>
    </thead>
    <tbody id="tarefas">

    </tbody>
  </table>
</section>
